// frontend/src/components/AdminDashboard.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AdminDashboard = () => {
  const [homeContent, setHomeContent] = useState({ youtubeUrl: '', description: '' });
  const [benefitsContent, setBenefitsContent] = useState({ youtubeUrl: '', description: '', images: [] });
  const token = localStorage.getItem('token');

  useEffect(() => {
    const fetchContent = async () => {
      try {
        const { data } = await axios.get('/api/admin/content', {
          headers: { Authorization: `Bearer ${token}` },
        });
        if (data) {
          setHomeContent(data.home || {});
          setBenefitsContent(data.benefits || {});
        }
      } catch (error) {
        console.error('Error fetching content:', error.response.data.message);
      }
    };
    fetchContent();
  }, [token]);

  const updateHome = async () => {
    try {
      const { data } = await axios.put(
        '/api/admin/content/home',
        homeContent,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setHomeContent(data.home);
      alert('Home content updated!');
    } catch (error) {
      console.error('Error updating home content:', error.response.data.message);
    }
  };

  const updateBenefits = async () => {
    try {
      const { data } = await axios.put(
        '/api/admin/content/benefits',
        benefitsContent,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setBenefitsContent(data.benefits);
      alert('Benefits content updated!');
    } catch (error) {
      console.error('Error updating benefits content:', error.response.data.message);
    }
  };

  return (
    <div className="admin-dashboard">
      <h2>Admin Dashboard</h2>
      <div>
        <h3>Home Content</h3>
        <input
          type="text"
          placeholder="YouTube URL"
          value={homeContent.youtubeUrl}
          onChange={(e) => setHomeContent({ ...homeContent, youtubeUrl: e.target.value })}
        />
        <textarea
          placeholder="Description"
          value={homeContent.description}
          onChange={(e) => setHomeContent({ ...homeContent, description: e.target.value })}
        ></textarea>
        <button onClick={updateHome}>Update Home Content</button>
      </div>
      <div>
        <h3>Benefits Content</h3>
        <input
          type="text"
          placeholder="YouTube URL"
          value={benefitsContent.youtubeUrl}
          onChange={(e) => setBenefitsContent({ ...benefitsContent, youtubeUrl: e.target.value })}
        />
        <textarea
          placeholder="Description"
          value={benefitsContent.description}
          onChange={(e) => setBenefitsContent({ ...benefitsContent, description: e.target.value })}
        ></textarea>
        <input
          type="text"
          placeholder="Images (comma separated paths)"
          value={benefitsContent.images.join(',')}
          onChange={(e) =>
            setBenefitsContent({ ...benefitsContent, images: e.target.value.split(',') })
          }
        />
        <button onClick={updateBenefits}>Update Benefits Content</button>
      </div>
    </div>
  );
};

export default AdminDashboard;
