// frontend/src/components/Home.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

// Helper function to extract YouTube video id from a URL
function extractVideoId(url) {
  try {
    const urlObj = new URL(url);
    return urlObj.searchParams.get('v') || url;
  } catch (error) {
    return url; // fallback if URL parsing fails
  }
}

const Home = ({ user }) => {
  // State to hold the home content fetched from the backend
  const [homeContent, setHomeContent] = useState({
    youtubeUrl: '',
    description: '',
  });

  useEffect(() => {
    // Fetch home content from the backend API
    const fetchHomeContent = async () => {
      try {
        const { data } = await axios.get('/api/content');
        if (data && data.home) {
          setHomeContent(data.home);
        }
      } catch (error) {
        console.error('Error fetching home content:', error.message);
      }
    };
    fetchHomeContent();
  }, []);

  return (
    <div className="home">
      <h1 className="home-title">Bhagavad Gita, The Song of God</h1>
      <p>
        {homeContent.description ||
          'Brief introduction on Bhagavad Gita scriptures... (Example text to be filled)'}
      </p>
      <div className="video-container">
        {homeContent.youtubeUrl ? (
          <iframe
            width="560"
            height="315"
            src={`https://www.youtube.com/embed/${extractVideoId(
              homeContent.youtubeUrl
            )}`}
            title="YouTube video player"
            frameBorder="0"
            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
            allowFullScreen
          ></iframe>
        ) : (
          <iframe
            width="560"
            height="315"
            src="https://www.youtube.com/embed/your_video_id"  // fallback video URL
            title="YouTube video player"
            frameBorder="0"
            allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
            allowFullScreen
          ></iframe>
        )}
      </div>
      {user && <p>Welcome back, {user.name}!</p>}
    </div>
  );
};

export default Home;
