// frontend/src/components/Home.js
import React from 'react';

const Home = ({ user }) => {
  return (
    <div className="home">
      <h1 className="home-title">Bhagavad Gita, The Song of God</h1>
      <p>
        Brief introduction on Bhagavad Gita scriptures... (Example text to be filled)
      </p>
      <div className="video-container">
        <iframe
          width="560"
          height="315"
          src="https://www.youtube.com/embed/your_video_id"  // Replace with your YouTube video id
          title="YouTube video player"
          frameBorder="0"
          allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
          allowFullScreen
        ></iframe>
      </div>
      {user && <p>Welcome back, {user.name}!</p>}
    </div>
  );
};

export default Home;
