// frontend/src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import Home from './components/Home';
import Benefits from './components/Benefits';
import Register from './components/Register';
import Login from './components/Login';
import AdminDashboard from './components/AdminDashboard';
import Chatbot from './components/Chatbot';

function App() {
  const [user, setUser] = useState(null); // store logged in user data
  const [showChatbot, setShowChatbot] = useState(false);

  // Build the background image URL using PUBLIC_URL
  const appStyle = {
    backgroundImage: `url(${process.env.PUBLIC_URL}/images/background.jpg)`,
    backgroundRepeat: 'no-repeat',
    backgroundPosition: 'center center',
    backgroundAttachment: 'fixed',
    backgroundSize: 'cover',
    minHeight: '100vh'
  };

  return (
    <div style={appStyle}>
      <Router>
        <div className="App">
          <Navbar user={user} setUser={setUser} />
          <Routes>
            <Route path="/" element={<Home user={user} />} />
            <Route path="/benefits" element={<Benefits />} />
            <Route path="/register" element={<Register />} />
            <Route path="/login" element={<Login setUser={setUser} />} />
            <Route path="/admin" element={<AdminDashboard />} />
          </Routes>
          {/* Display chatbot only for logged in non-admin users */}
          {user && user.role !== 'admin' && (
            <div className="chatbot-container">
              <div className="chatbot-icon" onClick={() => setShowChatbot(!showChatbot)}>
                <img src="/chatbot-icon.png" alt="Chatbot" />
              </div>
              {showChatbot && <Chatbot />}
            </div>
          )}
        </div>
      </Router>
    </div>
  );
}

export default App;
